import React from 'react';
import { Route } from 'react-router-dom';
import {connect} from "react-redux";

import CollectionsOverview from "../../components/collections-overview/collections-overview.comp";
import CollectionPage from "../collection/collection.comp";
import {firestore, convertCollectionsSnapShotToMap} from "../../firebase/firebase.utils";
import {updateCollections} from "../../redux/shop/shop.actions";
import WithSpinner from "../../components/with-spinner/with-spinner.component";

import './shop.styles.scss';
// import * as PropTypes from "prop-types";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {
    state = {
        loading: true
    };

    // unsubscribeFromSnapShot = null;

    componentDidMount() {
        const {updateCollections} = this.props;
        const collectionRef = firestore.collection('collections');

        collectionRef.get().then(response =>{
            const collectionMap = convertCollectionsSnapShotToMap(response);
            updateCollections(collectionMap);
            this.setState({loading: false});
        })


        // this.unsubscribeFromSnapShot = collectionRef.onSnapshot(async snapShot => {
        //     const collectionsMap = convertCollectionsSnapShotToMap(snapShot);
        //     updateCollections(collectionsMap);
        //     this.setState({loading: false});
        // });
    }

    // componentWillUnmount() {
    //     this.unsubscribeFromSnapShot();
    // }

    render() {
        let {match} = this.props;
        return (
            <div className='shop-page'>
                <Route exact path={`${match.path}`} render={(props =>
                    <CollectionsOverviewWithSpinner isLoading={this.state.loading} {...props}/>)}/>
                <Route exact path={`${match.path}/:collectionId`} render={props =>
                <CollectionPageWithSpinner isLoading={this.state.loading} {...props}/>}/>
            </div>
        );
    }
}

// ShopPage.propTypes = {match: PropTypes.any};

const mapDispatchToProps = dispatch => ({
    updateCollections: collections => dispatch(updateCollections(collections))
});


export default connect(null, mapDispatchToProps)(ShopPage);
